private void ceker_banyakKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_ceker_banyakKeyReleased
        int hasil;
        int a = Integer.parseInt(ceker_banyak.getText());
        hasil = a * 12000;
        ceker_jumlah.setText(Integer.toString(hasil));
}//GEN-LAST:event_ceker_banyakKeyReleased

