private void pesanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pesanActionPerformed
       int a,b,c,d,e,f,g,h,i,j;
       a = Integer.parseInt(ceker_jumlah.getText());
       b = Integer.parseInt(usus_jumlah.getText());
       c = Integer.parseInt(sayap_jumlah.getText());
       d = Integer.parseInt(kepala_jumlah.getText());
       e = Integer.parseInt(ayam_jumlah.getText());
       f = Integer.parseInt(mie_jumlah.getText());
       g = Integer.parseInt(paket_jumlah.getText());
       h = Integer.parseInt(nasi_jumlah.getText());
       i = Integer.parseInt(air_jumlah.getText());
       j = Integer.parseInt(teh_jumlah.getText());
       int hasil;
       hasil = a + b + c + d + e + f + g + h + i + j;
       jumlah.setText(Integer.toString(hasil));
}//GEN-LAST:event_pesanActionPerformed

